import useAuth from '../contexts/auth';

const Home = () => {

  const [homeDefaultImage, sethomeDefaultImage] = useState<ImageData>();
  const { state: { isLoading }, dispatch } = useAuth();

  useEffect(() => {
    if (!homeDefaultImage) {
      const fetchData = async (): Promise<void> => {
        dispatch(createActionLoading(true));
        getDefaultUserImage()
        .then(imageData => { 
          setuserDefaultImage(imageData);
        }) 
        .catch(error => {
          throw new Error(error);
        });
        UserApiService.getUserById(userId!)
          .then(user => setUserDisplayed(user))
          .catch((apiErrors: IErrors) => handleFetchUserError(apiErrors));

        dispatch(createActionLoading(false));
      }
      fetchData();  
    }
  // eslint-disable-next-line
  }, [user]);

  return (  
    <div className="Home">  

    </div>  
  );  
}

export default Home;
