import jwtDecode from 'jwt-decode';
import { JWTPayload } from '../types';
import AuthApiService from '../services/api/AuthApiService';
import TokenService from '../services/api/TokenService';

function getSessionDuration(token: any): number {
    const decoded_jwt: JWTPayload = jwtDecode<JWTPayload>(token); 
    return decoded_jwt.sessionDuration;
}

export function wasUserActiveDuringCurrentSession(token: string): boolean {
    try {
        const sessionDuration = getSessionDuration(token);
        const lastResponseTimeStamp = TokenService.getHttpResponseTimeStamp();
        const lastRequestTime = lastResponseTimeStamp.valueOf() / 1000;
        const current_time = Date.now().valueOf() / 1000;
        const lastAccessDelay = current_time - lastRequestTime;
        return lastAccessDelay < sessionDuration;
    } catch (_) {
      return false;
    }
}

export function getSessionRemainingTimeBeforeExpiration(token: string) {
    try {
      const decoded_jwt: JWTPayload = jwtDecode<JWTPayload>(token);
      const current_time = Date.now().valueOf() / 1000;
      return Math.min(0, decoded_jwt.exp - current_time);
    } catch (_) {
      return 0;
    }
}

export function automaticSessionRenewal(token: string) {
    const sessionDuration = getSessionDuration(token);
    const remainingTime = getSessionRemainingTimeBeforeExpiration(token);
    if (remainingTime < sessionDuration * 0.10 && wasUserActiveDuringCurrentSession(token)) {
        AuthApiService.extendUserSession(sessionDuration);
    }
}
