import API, { TOKEN_KEY , setToken } from './APIUtils';
import { IUser, User } from '../../types';
import { setLocalStorage } from '../../utils/utils';

function handleUserData(user: User): User {
  setLocalStorage(TOKEN_KEY, user.token!);
  setToken(user.token!);
  return user;
}

function getCurrentUser() {
  return API.get<User>('/auth/user');
}

function login(email: string, password: string) {
  return API.post<User>('/auth/login', {
    user: { email, password },
  }).then((response) => handleUserData(response.data));
}

function register(
  username: string,
  email: string,
  password: string,
) : Promise<User> {
  const user: User = {username, email, password};
  return API.post<User>('/auth/register', { user })
    .then((response) => handleUserData(response.data))
  .catch((error) => {
    console.log(error);
  });
}

function logout() {
  localStorage.removeItem(TOKEN_KEY);
  setToken(null);
}

const AUTHAPI = {getCurrentUser, login, register, logout}

export default AUTHAPI
