import React, { useState } from 'react';
import { ImageData, ImageSizeProps } from '../../types';
import { resizeImage } from '../../utils/image.utils';
import Image from '../common/Image';

export interface ImageResizeProps {
    imageData: ImageData,
    resize: ImageSizeProps,
}

const ImageResize = async (props: ImageResizeProps) => {

    let imageResized: ImageData | undefined = undefined;
    const propImage: ImageData = {...props.imageData};

    const handleOnError = (error: any) => {
        throw new Error(error);
    }

    await resizeImage(propImage, props.resize.maxWidth, props.resize.maxHeight)
        .then(imageData => { imageResized = imageData; });      

    return ( imageResized &&
        <>
        { <Image imageData={imageResized} onError={handleOnError}/> }
        </>
    ) 
}

export default ImageResize;
