import 'bootstrap/dist/css/bootstrap.min.css';  
import React from 'react';
import {CardDeck } from 'react-bootstrap';
import { IPost } from "../../types";
import { useNavigate } from 'react-router-dom';
import ViewBlogCard, { onViewBlog } from './ViewBlogCard';

type ViewBlogCardsProps = React.HTMLProps<HTMLElement> & {
  posts: IPost[],
}

const ViewPostCards: React.FC<ViewBlogCardsProps> = ({className, posts}) => {

  const navigate = useNavigate();

  const handleViewBlog: onViewBlog = (postId: string)=>{
    navigate(`/blog/${postId}`);
  }

  return (  
    <CardDeck style={{display: 'flex', flexDirection: 'row'}}>
      {
        posts && posts.map((post: IPost) =>    
        (
          <div  key={post.id}>
            <ViewBlogCard style={{flex: 1}} post={post} className={className} onViewBlog={handleViewBlog}/>
          </div>
        ))
      }
    </CardDeck>
  );  
};

export default ViewPostCards;
