import jwtDecode from 'jwt-decode';
import API from './APIService';
import { User, ILogin, IRegister, JWTPayload, ISessionExtension } from '../../types';
import TokenService from './TokenService';

export function isTokenValid(token: string) {
  try {
    const decoded_jwt: JWTPayload = jwtDecode<JWTPayload>(token);
    const current_time = Date.now().valueOf() / 1000;
    return decoded_jwt.exp > current_time;
  } catch (_) {
    return false;
  }
}

export function isSessionInactiveForSessionDuration(token: string) {
  // Session could be expired if user did not made any request to the server since
  // the last time the session was set/reset. Otherwise, just refresh the session
  try {
    const decoded_jwt: JWTPayload = jwtDecode<JWTPayload>(token);
    const lastResponseTimeStamp = TokenService.getHttpResponseTimeStamp();
    const lastRequestTime = lastResponseTimeStamp.valueOf() / 1000;
    const lastRequestExpirationTime = lastRequestTime + decoded_jwt.sessionDuration;
    const current_time = Date.now().valueOf() / 1000;
    return lastRequestExpirationTime < current_time;
  } catch (_) {
    return true;
  }
}

export function getSessionDurationExtension(token: string) {
  try {
    const decoded_jwt: JWTPayload = jwtDecode<JWTPayload>(token);
    const lastResponseTimeStamp = TokenService.getHttpResponseTimeStamp();
    const lastRequestTime = lastResponseTimeStamp.valueOf() / 1000;
    const current_time = Date.now().valueOf() / 1000;
    const lastAccessDelay = current_time - lastRequestTime;
    return decoded_jwt.sessionDuration - lastAccessDelay;
  } catch (_) {
    return 0;
  }
}

function getCurrentUser(): User {
  return TokenService.getUser();
};

async function login(email: string, password: string): Promise<User> {
  const loginParms: ILogin = { email, password }
  return API.put<User>('/auth/login', loginParms)
    .then(response => {
      TokenService.setUser(response.data);
      return response.data;
    });
}

async function register(username: string, email: string, password: string) : Promise<User> {
  const registerParms: IRegister = { username, email, password }
  return API.post<User>('/auth/register', registerParms)
    .then(response => {
      TokenService.setUser(response.data);
      return response.data;
    });
}

async function refresh() : Promise<User> {
  return API.put<User>('/auth/session/refresh')
    .then(response => {
      TokenService.setUser(response.data);
      return response.data;
    });
}

async function extendUserSession(extension: number) : Promise<User> {
  const sessionExtensionParms: ISessionExtension = { extension };
  return API.put<User>('/auth/session/extend', sessionExtensionParms)
    .then(response => {
      TokenService.setUser(response.data);
      return response.data;
    });
}

async function getUserProfile() : Promise<User> {
  return API.get<User>('/auth/profile')
    .then(response => response.data);
}

async function updateUserProfile(user: User) : Promise<User> {
  return API.put<User>('/auth/profile', user)
    .then(response => response.data);
}

function logout() {
  TokenService.removeUser();
}

const AUTHAPI = {getCurrentUser, login, register, refresh, logout, getUserProfile, updateUserProfile, 
                  getSessionDurationExtension, extendUserSession}

export default AUTHAPI
