import React, { useState } from 'react';
import {useModalContext} from "../../contexts/modalContext";
import Modal from "react-bootstrap/Modal";

type DisplayButtonProps = React.HTMLProps<HTMLButtonElement> & {
    contentToDisplay: string | JSX.Element,
}

const DisplayModalButton: React.FC<DisplayButtonProps> = ({children, contentToDisplay, className, onClick}) => {

    const modalContext = useModalContext();

    const [showModal, setShowModal] = useState<boolean>();
/* 
    const handleOnClick = async (event: React.MouseEvent<HTMLButtonElement>) => {
        await modalContext.showModal(contentToDisplay);
        onClick && onClick(event);    
    }; */


    const handleOnClick = async (event: React.MouseEvent<HTMLButtonElement>) => {
        setShowModal(true);
        onClick && onClick(event);    
    };

    return (
        <button className={className} onClick={handleOnClick}>
            {children}
            <Modal show={showModal}>
                <Modal.Header>Hi</Modal.Header>
                <Modal.Body>asdfasdf</Modal.Body>
                <Modal.Footer>This is the footer</Modal.Footer>
           </Modal>
        </button>
    )
};

export default DisplayModalButton;
