import 'bootstrap/dist/css/bootstrap.min.css';  
import React from 'react';
import {Container ,Card, Col, Button} from 'react-bootstrap';
import useAuth from '../../contexts/auth';
import { Link, useNavigate } from 'react-router-dom';
import { IPost, UserRole } from "../../types";
import DeleteButton from '../common/deleteConfirmation';
import { createActionLoading } from '../../reducers/auth';
import { PostApiService } from "../../services/api/PostApiService";


type ViewCardProps = React.HTMLProps<HTMLElement> & {
  post: IPost,
  onViewPostDetail: onViewPostDetail,
}

const navigate = useNavigate();
const { state, dispatch } = useAuth();
const isAdministrator = () => state.isAuthenticated && state.user?.role === UserRole.ADMIN;
const deletePostMessage = (post: IPost) => `${post.title} post`;

export type onViewPostDetail = (postId: string) => void;

const ViewCard: React.FC<ViewCardProps> = ({className, post, onViewPostDetail}) => {



  return (  
    <div className="ViewCard">  
      <Container className='p-4'>  
          <Card>  
            {/* <Card.Img variant="top" src={post.image?.data} />   */}
            <Card.Body>
              <Card.Header>
                <span>{post.user?.username}</span>
              </Card.Header> 
              <Card.Title>
                <br/>
                {post.title}
              </Card.Title>
              <Card.Subtitle>
                <br/>
                <span>Description:</span> 
              </Card.Subtitle>    
              <Card.Text>
                <br/>
                {post.description}
              </Card.Text>  
              <Button className={className} onClick={() => onViewPostDetail(post.id!)}>View</Button>  
            </Card.Body>  
          </Card>  
      </Container>  
    </div>  
  );  
};

export default ViewCard;
