import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { IUser } from "../../types";
import { toast } from "react-toastify";
import { UserApiService } from "../../services/api/UserApiService";
import { createActionLoading } from '../../reducers/auth';
import useAuth from '../../contexts/auth';
import ListErrors from '../common/ListErrors';
import { IErrors } from '../../types';
import { toLocalDateString } from '../../utils/utils';

const ViewUser = () => {

  const { userId } = useParams<{ userId: string }>();
  const { dispatch } = useAuth();
  const [user, setUser] = useState<IUser>();
  const [errors, setErrors] = React.useState<IErrors | null>();

  useEffect(() => {
    if (!user) {
      const fetchData = async (): Promise<void> => {
        dispatch(createActionLoading(true));
        await UserApiService.getUserById(userId!)
        .then(user => setUser(user))
        .catch((apiErrors: IErrors) => handleFetchUserError(apiErrors))
        dispatch(createActionLoading(false));
      }
      fetchData();  
    }
  // eslint-disable-next-line
  }, []);

  const handleFetchUserError = (apiErrors: IErrors) => {
    toast.error(`User reading failed, see error list`);
    setErrors(apiErrors);
  }

    return (
        <section className="user-area">
        {errors && <ListErrors errors={errors} />}
        <div className="container">
          <div className="row">
            <div className="col-lg-1 col-md-0" />
            <div className="col-lg-10 col-md-12">
              {user && 
              (
                <div className="main-user">
                  <div className="user-top-area">
                      <div>
                        <br/>
                         <h4 className="username">
                          <span>
                            Name:
                          </span>
                        </h4>
                        <h5>{user.username}</h5>
                        <br/>
                        { user.createdOn &&
                          <h5 className="createdOn">
                            <span>
                              <b>Created on {toLocalDateString(user.createdOn)}</b>
                            </span>
                            <br/> 
                          </h5>                        
                        }
                        <h4 className="email">
                          <span>
                            Email:
                          </span>
                        </h4>
                        <h5>{user.email}</h5>
                        <br/>
                        <h4 className="role">
                          <span>
                            Role: 
                          </span>
                         </h4>
                        <h5>{user.role}</h5>
                       </div>
                  </div>
                </div>   
                )           
              }
            </div>
          </div>
        </div>
      </section>
    );
}

export default ViewUser;
