import React from 'react';
import useAuth from '../../contexts/auth';
import {useConfirmationModalContext} from "../../contexts/modalConfirmationContext";

async function sessionTimeoutConfirmation(): Promise<boolean> {
    const modalContext = useConfirmationModalContext();

    return modalContext.showConfirmation(
        'Session expired',
        <div style={{border: "2px solid blue", padding: "10px"}}>
            <p>
                Do you want to stay logged in?
            </p>
        </div>
    );
}

type SessionTimeoutHandlerProps = React.HTMLProps<HTMLElement> & {
    show: boolean
}
const SessionTimeoutHandler = () => {
    const modalContext = useConfirmationModalContext();
    const {state} = useAuth();

    return modalContext.showConfirmation(
        'Session expired',
        <div style={{border: "2px solid blue", padding: "10px"}}>
            <p>
                Do you want to stay logged in?
            </p>
        </div>
    );

    return (<></>)
};

export default SessionTimeoutHandler;
