import React, { useRef } from "react";
import JoditEditor from "jodit-react";

type ViewPostContentProps = React.HTMLProps<HTMLElement> & {
    content: string,
    onUpdatePostContent: onUpdatePostContent,
    onEndEditing: onEndEditing,
  }

export type onUpdatePostContent = (content: string) => void;
export type onEndEditing = () => void;

const EditPostContent: React.FC<ViewPostContentProps> = ({className, content, onUpdatePostContent, onEndEditing}) => {

    const editor = useRef(null);
    const config = {
        readonly: false,
        height: 400
    };

    const handleUpdateContent = (value: string) => {
        onUpdatePostContent(value);
    };

    return (
        <div className="App">
        <h1>React Editors</h1>
        <h2>Start editing to see some magic happen!</h2>
        <button className="btn btn-secondary col-md-2"  onClick={onEndEditing} >
                      Save Content
        </button>  
        <JoditEditor
            ref={editor}
            value={content}
            config={config}
            onChange={handleUpdateContent}
        />
        <div dangerouslySetInnerHTML={{ __html: content }} />
        </div>
    );
}

export default EditPostContent;
