import React from 'react';
import { ImageData } from '../../types';

export interface ImageUploadProps {
    onImageUpload: (imageData: ImageData) => void;
}

const ImageUpload = (props: ImageUploadProps) => {

    const encode = (str: string):string => Buffer.from(str, 'binary').toString('base64');

    // const getBase64 = (file: File) => {
    //     return new Promise<string>((resolve,reject) => {
    //        const reader = new FileReader();
    //        reader.onload = () => resolve(reader.result as string);
    //        reader.onerror = error => reject(error);
    //        reader.readAsDataURL(file);
    //     });
    // }

    // const getBinaryString = (file: File) => {
    //     return new Promise<string>((resolve,reject) => {
    //        const reader = new FileReader();
    //        reader.onload = () => resolve(reader.result as string);
    //        reader.onerror = error => reject(error);
    //        reader.readAsBinaryString(file);
    //     });
    // }

    const getArrayBuffer = (file: File) => {
        return new Promise<ArrayBuffer>((resolve,reject) => {
           const reader = new FileReader();
           reader.onload = () => resolve(reader.result as ArrayBuffer);
           reader.onerror = error => reject(error);
           reader.readAsArrayBuffer(file);
        });
    }

    const onImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            let img = event.target.files[0];
            getArrayBuffer(img).then(data => {
                const imageData: ImageData = { data: Buffer.from(data), contentType: img.type};
                const imageString = imageData.data.toString('base64');
                props.onImageUpload(imageData);
            });
            // getBase64(img).then(data => {
            //     const imageString = data;
            //     // props.onImageUpload(imageData);
            // });
            // getBinaryString(img).then(data => {
            //     const imageString = encode(data);
            //     // props.onImageUpload(imageData);
            // });
        }
    };

    return (
            <input className="col-md-3" type="file" id="imageFile" name='imageFile' accept=".jpeg, .png, .jpg"  
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => onImageChange(e)} />
    )

}

export default ImageUpload;
