import React from "react";

type ViewPostContentProps = React.HTMLProps<HTMLElement> & {
    content: string,
    onClose: onClose,
}

export type onClose = () => void;

const ViewPostContent: React.FC<ViewPostContentProps> = ({className, content, onClose}) => {

  const handleClose = () => {
    if (onClose) {
      onClose();
    }
  };
  
    return (
      <div className="container">

          <div>
            <div className="row">
              <div dangerouslySetInnerHTML={{__html: content}} />           
            </div>
            <div className="row">
              <div className="col-lg-10 col-md-12">
                <div className="form-group row-md-6 pull-right">
                  <button className="btn ml-2 btn-secondary" onClick={ () => handleClose() } >
                    Close
                  </button>
                </div>
              </div>
            </div>
          </div>
      </div>
     );
}

export default ViewPostContent;
