import 'bootstrap/dist/css/bootstrap.min.css';  
import React from 'react';
import {Container ,Card, Button } from 'react-bootstrap';
import { IUser, ImageSizeProps, ImageData } from "../../types";
import Image from '../common/Image';
import ImageResize from '../common/ImageResize';

type ViewUserCardProps = React.HTMLProps<HTMLElement> & {
  user: IUser,
  defaultUserImage: ImageData,
}

const ViewUserCard: React.FC<ViewUserCardProps> = ({className, user, defaultUserImage}) => {

  const imageMaxSize: ImageSizeProps = {maxWidth:120, maxHeight:120};

  const UserImage = () => {
    if(user.image) {
      return <ImageResize imageData={user.image} resize={imageMaxSize}/>;
    }  else {
      return  defaultUserImage && <Image imageData={defaultUserImage}/> 
    }
  }

  return (  
    <div className="view-user-card">  
      <Container>  
          <Card style={{ width: '12.5rem',  cursor: "pointer" }}>  
            <Card.Body>
              <Card.Header>
                <h5>{user?.username}</h5>
                {UserImage()}
              </Card.Header> 
              <Card.Text>
                <h6>{user.email}</h6>
              </Card.Text>
            </Card.Body>  
          </Card>  
      </Container>  
    </div>  
  );  
};

export default ViewUserCard;
