import jwtDecode from 'jwt-decode';
import API, { setToken } from './APIUtils';
import { User } from '../../types';
import { setLocalStorage, removeLocalStorage, getLocalStorageValue } from '../../utils/utils';
import { IAuthToken } from '../../types';

export const USER_KEY = 'user';
export const TOKEN_KEY = 'token';

type JWTPayload = {
  id: string;
  username: string;
  exp: number;
};

export function isTokenValid(token: string) {
  try {
    const decoded_jwt: JWTPayload = jwtDecode(token);
    const current_time = Date.now().valueOf() / 1000;
    return decoded_jwt.exp > current_time;
  } catch (error) {
    return false;
  }
}

function handleUserAuth(user: User) {
  setLocalStorage(USER_KEY, user);
  if (user.token) {
    setLocalStorage(TOKEN_KEY, user.token);
    setToken(JSON.stringify(user.token));  
  }
}

function getCurrentUser() {
  return getLocalStorageValue(USER_KEY);
};

async function login(email: string, password: string): Promise<User> {
  return new Promise((resolve, reject) => {
    API.post<User>('/auth/login', { email, password })
      .then(response => {
        handleUserAuth(response.data);
        resolve(response.data);
      })
      .catch(error => {
        reject(error);
      });
  });
}

async function register(username: string, email: string, password: string) : Promise<User> {
  return new Promise((resolve, reject) => {
    API.post<User>('/auth/register', { username, email, password })
      .then(response => {
        handleUserAuth(response.data);
        resolve(response.data);
      })
      .catch(error => {
        reject(error);
      });
  });
}

function logout() {
  removeLocalStorage(USER_KEY);
  removeLocalStorage(TOKEN_KEY);
  setToken(null);
}

const AUTHAPI = {getCurrentUser, login, register, logout}

export default AUTHAPI
